export const loginSlice = createSlice({
  name: "login",
  initialState: {
    isConnected: false,
    firstName: "",
    lastName: "",
  },
  reducers: {
    login: (state) => {
      ;(state.isConnected = true), (state.firstName = userEmail)
    },
    logout: (state) => {
      state.isConnected = false
    },
