import { createSlice } from "@reduxjs/toolkit"

export const loginSlice = createSlice({
  name: "login",
  initialState: {
    isConnected: false,
    // firstname:'',
    // lastname:'',
  },
  reducers: {
    login: (state) => {
      state.isConnected = true
    },
    logout: (state) => {
      state.isConnected = false
    },
  },
})

export const { login, logout } = loginSlice.actions
export default loginSlice.reducer
