import "./style.scss"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faCircleUser } from "@fortawesome/free-solid-svg-icons"
import { useForm } from "react-hook-form"
import { useNavigate } from "react-router-dom"
import { useDispatch, useSelector } from "react-redux"
import { login } from "../../feature/loginSlice"
import axios from "axios"

const Form = () => {
  const dispatch = useDispatch()

  const [userEmail, setUserEmail] = useState("")
  const [userPassword, setUserPassword] = useState("")

  const { register, handleSubmit } = useForm()
  const navigate = useNavigate()

  const onSubmit = () => {
    axios
      .post("http://localhost:3001/api/v1/user/login", {
        email: `${userEmail}`,
        password: `${userPassword}`,
      })

      .then((res) => {
        dispatch(login())
        localStorage.setItem("token", res.data.body.token)
        navigate("/user")
      })
  }

  // useEffect(() => {
  // axios
  //   .post("http://localhost:3001/api/v1/user/login", {
  //     email: `${userEmail}`,
  //     password: `${userPassword}`,
  //   })
  //   // .then((res) => setUserToken(res.data.body.token))
  //   .then((res) => dispatch(setLoginData(res.data)))
  // .then((res) => console.log(res.data))
  // }, [onSubmit, userEmail, userPassword, userToken])

  // if (login.length !== 0) {
  //   console.log("we have a token")
  //   localStorage.setItem("token", userToken)

  //   navigate("/user")
  //   window.location.reload()
  // } else {
  //   console.log("we don't have a token")
  // }

  return (
    <section className="sign-in-content">
      <FontAwesomeIcon icon={faCircleUser} className="sign-in-icon" />
      <h1>Sign In</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="input-wrapper">
          <label htmlFor="username">
            Username
            <input
              id="username"
              // {...register("username", { required: true })}
              onChange={(e) => setUserEmail(e.target.value)}
              value={userEmail}
            />
          </label>
        </div>
        <div className="input-wrapper">
          <label htmlFor="password">
            Password
            <input
              id="password"
              // {...register("password", { required: true })}
              onChange={(e) => setUserPassword(e.target.value)}
              value={userPassword}
            />
          </label>
        </div>
        <div className="input-remember">
          <label htmlFor="remember-me">
            Remember me
            <input type="checkbox" {...register("remember-me")} />
          </label>
        </div>
        <button type="submit" value="submit" className="sign-in-button">
          Sign In
        </button>
      </form>
    </section>
  )
}

export default Form
